import axios from 'axios';
import React, { useEffect, useState } from 'react';

export default function ConverterCurrencyPair() {
  const [amount, setAmount] = useState(null);
  const [inputName, setInputName] = useState('');
  const [from, setFromCurrency] = useState('');
  const [to, setToCurrency] = useState('');

  function handleCurrencyInput(e) {
    setAmount(e.target.value);
    setInputName(e.target.name);
  }

  function handleFromCurrency(e) {
    console.log(e.target.value);
  }

  function handleToCurrency(e) {
    console.log(e.target.value);
  }

  function handleRateRequest(amount, from, to) {
    if (amount && from && to) {
      axios
        .get(
          `https://api.getgeoapi.com/v2/currency/convert?api_key=77eed905a93a78d05adb55ce7db36955d7be8453&from=${from}&to=${to}&amount=${amount}`
        )
        .then((res) => console.log(res.data));
    }
  }

  useEffect(() => {
    handleRateRequest();
  }, [amount, from, to]);

  return (
    <div>
      <input type="text" name="left" value={amount} onChange={handleCurrencyInput} />
      <select name="left" id="left" onChange={handleFromCurrency}>
        <option selected value="" disabled>
          {' '}
          -- select currency --{' '}
        </option>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="UAH">UAH</option>
        <option value="RON">RON</option>
      </select>
      <select name="right" id="right" onChange={handleToCurrency}>
        <option selected value="" disabled>
          {' '}
          -- select currency --{' '}
        </option>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="UAH">UAH</option>
        <option value="RON">RON</option>
      </select>
      <input type="text" name="right"></input>
    </div>
  );
}
